import React, { useState, useEffect, useCallback } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import { loadF1Data, getCurrentStandings, getUpcomingRaces } from '../utils/f1Data';
import { calculateScenario } from '../utils/calculations';

const ScenarioForm = ({ setScenarioResults }) => {
  const [standings, setStandings] = useState([]);
  const [upcomingRaces, setUpcomingRaces] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedRace, setSelectedRace] = useState('');

  useEffect(() => {
    const initializeData = async () => {
      try {
        await loadF1Data();
        setStandings(getCurrentStandings());
        const races = getUpcomingRaces();
        setUpcomingRaces(races);
        if (races.length > 0) {
          setSelectedRace(races[0].name);
        }
      } catch (error) {
        console.error('Error initializing data:', error);
      } finally {
        setLoading(false);
      }
    };

    initializeData();
  }, []);

  const onDragEnd = useCallback((result) => {
    if (!result.destination) return;

    const items = Array.from(standings);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    const reorderedResults = items.map((item, index) => ({
      ...item,
      position: index + 1
    }));

    setStandings(reorderedResults);
  }, [standings]);

  const handleSubmit = (e) => {
    e.preventDefault();
    const results = calculateScenario(selectedRace, standings);
    setScenarioResults(results);
  };

  if (loading) return <div className="loading">Loading data...</div>;

  return (
    <div className="scenario-form">
      <h2>Create Race Scenario</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="race-select">Select Race:</label>
          <select
            id="race-select"
            value={selectedRace}
            onChange={(e) => setSelectedRace(e.target.value)}
            required
          >
            {upcomingRaces.map(race => (
              <option key={race.id} value={race.name}>
                {race.name} ({race.circuit})
              </option>
            ))}
          </select>
        </div>

        <div className="race-results-input">
          <h3>Drag drivers to set finishing order:</h3>
          <DragDropContext onDragEnd={onDragEnd}>
            <Droppable droppableId="drivers">
              {(provided) => (
                <div
                  {...provided.droppableProps}
                  ref={provided.innerRef}
                  className="driver-list"
                >
                  {standings.map((driver, index) => (
                    <Draggable
                      key={driver.id}
                      draggableId={driver.id}
                      index={index}
                    >
                      {(provided) => (
                        <div
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          className="driver-item"
                        >
                          <span className="position">{index + 1}.</span>
                          <div className="driver-info">
                            <span className="driver-name">{driver.name}</span>
                            <span className="driver-team">{driver.team}</span>
                          </div>
                        </div>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          </DragDropContext>
        </div>

        <div className="form-actions">
          <button type="submit" className="calculate-btn">
            Calculate Scenario
          </button>
        </div>
      </form>
    </div>
  );
};

export default ScenarioForm;
