import React from 'react';
import { Link } from 'react-router-dom';

const Header = ({ setCurrentView }) => {
  return (
    <header className="app-header">
      <div className="header-content">
        <Link to="/" className="logo-link">
          <div className="logo-container">
            <h1 className="main-title">
              <span className="f1-red">F1</span>
              <span className="title-main">Interactive</span>
              <span className="title-sub">Championship Predictor</span>
            </h1>
            <div className="season-badge">2025 Season</div>
          </div>
        </Link>
        <nav className="main-nav">
          <button onClick={() => setCurrentView('leaderboard')}>Standings</button>
          <button onClick={() => setCurrentView('scenario')}>Race Predictor</button>
          <button onClick={() => setCurrentView('calculator')}>Points Calculator</button>
        </nav>
      </div>
      <div className="header-tagline">
        Simulate race outcomes and predict the 2025 F1 championship battle
      </div>
    </header>
  );
};

export default Header;
