import React, { useState } from "react";
import { pointsTable } from "../data";

function RaceSimulator({ drivers, onSimulate }) {
  const [results, setResults] = useState({});

  const handleSelect = (position, driverId) => {
    setResults((prev) => ({
      ...prev,
      [driverId]: pointsTable[position] || 0,
    }));
  };

  const handleSimulate = () => {
    onSimulate(results);
    setResults({});
  };

  return (
    <div className="mb-6">
      <h2 className="text-2xl font-semibold mb-4">Simulate Race</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4">
        {Object.entries(pointsTable).map(([position, points]) => (
          <div key={position} className="border p-3 rounded">
            <label className="block font-medium mb-1">
              Position {position} ({points} pts)
            </label>
            <select
              className="w-full p-2 border rounded"
              onChange={(e) => handleSelect(position, e.target.value)}
              defaultValue=""
            >
              <option value="" disabled>
                Select Driver
              </option>
              {drivers.map((driver) => (
                <option key={driver.id} value={driver.id}>
                  {driver.name}
                </option>
              ))}
            </select>
          </div>
        ))}
      </div>
      <button
        onClick={handleSimulate}
        className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Simulate Results
      </button>
    </div>
  );
}

export default RaceSimulator;
