import React, { useEffect, useState } from 'react';
import { loadF1Data, getCurrentStandings } from '../utils/f1Data';

const Leaderboard = ({ customization }) => {
  const [standings, setStandings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        await loadF1Data();
        setStandings(getCurrentStandings());
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div className="loading">Loading standings...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="leaderboard-container">
      <h2>Current Championship Standings</h2>
      <table className="leaderboard-table">
        <thead>
          <tr>
            <th>Position</th>
            <th>Driver</th>
            {customization.showTeams && <th>Team</th>}
            <th>Points</th>
            {customization.showWins && <th>Wins</th>}
            {customization.showPodiums && <th>Podiums</th>}
          </tr>
        </thead>
        <tbody>
          {standings.map(driver => (
            <tr key={driver.id} className={`driver-row ${driver.team.toLowerCase().replace(/\s+/g, '-')}`}>
              <td>{driver.position}</td>
              <td>
                <span className="driver-number">{driver.number}</span>
                {driver.name}
              </td>
              {customization.showTeams && <td>{driver.team}</td>}
              <td className="points">{driver.points}</td>
              {customization.showWins && <td>{driver.wins}</td>}
              {customization.showPodiums && <td>{/* Need to calculate podiums */}</td>}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
