import React, { useState } from "react";
import { driversData, racesRemaining } from "./data";
import Leaderboard from "./components/Leaderboard";
import RaceSimulator from "./components/RaceSimulator";
import ChampionshipCalculator from "./components/ChampionshipCalculator";

function App() {
  const [leaderboard, setLeaderboard] = useState(driversData);

  const updateLeaderboard = (newResults) => {
    const updated = leaderboard.map((driver) => {
      const pointsGained = newResults[driver.id] || 0;
      return {
        ...driver,
        points: driver.points + pointsGained,
      };
    });

    updated.sort((a, b) => b.points - a.points);
    setLeaderboard(updated);
  };

  return (
    <div className="p-4 max-w-5xl mx-auto">
      <h1 className="text-3xl font-bold mb-6">F1 Interactive Leaderboard</h1>
      <Leaderboard drivers={leaderboard} />
      <RaceSimulator drivers={leaderboard} onSimulate={updateLeaderboard} />
      <ChampionshipCalculator drivers={leaderboard} racesLeft={racesRemaining} />
    </div>
  );
}

export default App;
